var Helpers = /** @class */function () {
  function Helpers() {}
  Helpers.sortCompetitorsByEvent = function (wcif, eventId) {
    // todo parse wcif filled by groupifier to get the groups
    wcif.persons = wcif.persons.sort(function (a, b) {
      var textA = a[eventId].group;
      var textB = b[eventId].group;
      if (textA === '') {
        return 1;
      }
      if (textB === '') {
        return -1;
      }
      return textA < textB ? -1 : textA > textB ? 1 : 0;
    });
  };
  Helpers.sortCompetitorsByName = function (wcif) {
    wcif.persons = wcif.persons.sort(function (a, b) {
      var textA = a.name.toUpperCase();
      var textB = b.name.toUpperCase();
      return textA < textB ? -1 : textA > textB ? 1 : 0;
    });
  };
  Helpers.nameOfCompetitor = function (wcif, registrantId) {
    var filter = wcif.persons.filter(function (p) {
      return p.registrantId === registrantId;
    });
    return filter.length === 1 ? filter[0].name : '';
  };
  return Helpers;
}();
export { Helpers };
